package com.example.ishtag.view;import android.content.Context;import android.graphics.Typeface;import android.util.AttributeSet;import android.widget.EditText;import android.widget.TextView;public class MyCEditText extends EditText {    public MyCEditText(Context context, AttributeSet attrs, int defStyle) {        super(context, attrs, defStyle);    }   public MyCEditText(Context context, AttributeSet attrs) {        super(context, attrs);    }   public MyCEditText(Context context) {        super(context);   }   public void setTypeface(Typeface tf, int style) {	                 super.setTypeface(Typeface.createFromAsset(getContext().getAssets(), "fonts/HelveticaNeue.dfont"));    }}