package com.example.utils;import android.content.Context;import android.graphics.Typeface;public class TypeFace {    public Typeface getTypeFace1(Context context){		Typeface face = Typeface.createFromAsset (context.getAssets() , "fonts/HelveticaNeue.dfont" );      return face;		    }    public Typeface getTypeFace2(Context context){		Typeface face1 = Typeface.createFromAsset (context.getAssets() , "fonts/MyriadPro-Regular.otf" );      return face1;		    }}